import { useContext } from "react";
import VideoFlipperContext from "../context";

import './index.css';
import { aspectRatios } from "../constant";

const Editor = () => {
  const { videoRef, cropperRef, showCropper, position, aspectRatio, handleMouseMove, handleMouseUp, handleMouseDown, handleTimeUpdate } = useContext(VideoFlipperContext);

  const getVideoWidth = () => {
    return `${videoRef.current.getBoundingClientRect().height * aspectRatios[aspectRatio]}px`
  };

  return (
    <div className="video-container" onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}>
      <video
        className="video"
        ref={videoRef}
        src="http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4"
        onTimeUpdate={handleTimeUpdate}
      />
      {showCropper && <div
        id="draggable"
        ref={cropperRef}
        className="cropper"
        style={{
          top: `${position.y}px`,
          left: `${position.x}px`,
          width: getVideoWidth(),
          height: "100%",
          aspectRatio: aspectRatio,
        }}
        onMouseDown={handleMouseDown}
      />}
    </div>
  );
};

export default Editor;
