import { useContext } from "react";
import VideoFlipperContext from "../context";

import './index.css';

const Preview = () => {
  const { aspectRatio, previewCanvasRef } = useContext(VideoFlipperContext);

  console.log('previewCanvasRef', previewCanvasRef);
  return (
    <div className="preview-container">
      <div className="preview-heading">Preview</div>
      <div className="" style={{ aspectRatio: aspectRatio, overflow: 'hidden' }}>
        <canvas ref={previewCanvasRef} />
      </div>
    </div>
  );
};

export default Preview;
