import { useEffect, useRef, useState } from "react";

import './VideoFlipper.css';
import { aspectRatios } from "./constant";
import RecordedPreview from "./RecordedPreview";
import Header from "./header";
import VideoFlipperContext from "./context";

const VideoFlipper = () => {
  const [activeTab, setActiveTab] = useState('editor');
  const [isPlaying, setIsPlaying] = useState(false);
  const [playbackRate, setPlaybackRate] = useState(1);
  const [volume, setVolume] = useState(1);
  const [currentTime, setCurrentTime] = useState(0);
  const [aspectRatio, setAspectRatio] = useState("9:18");
  const [showCropper, setShowCropper] = useState(false);
  const [recordedData, setRecordedData] = useState([]);
  const [cropperSize, setCropperSize] = useState({ width: 0, height: 0 });
  const cropperRef = useRef(null);
  const videoRef = useRef(null);
  const previewCanvasRef = useRef(null);

  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [offset, setOffset] = useState({ x: 0, y: 0 });

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.playbackRate = playbackRate;
      videoRef.current.volume = volume;
    }
  }, [playbackRate, videoRef, volume]);

  useEffect(() => {
    if (videoRef.current) {
      setShowCropper(true);
    }
  }, [videoRef]);

  useEffect(() => {
    const updatePreview = () => {
      if (videoRef.current && cropperRef.current && previewCanvasRef.current) {
        const video = videoRef.current;
        const cropper = cropperRef.current;
        const canvas = previewCanvasRef.current;
        const ctx = canvas.getContext('2d');

        const cropperRect = cropper.getBoundingClientRect();
        const videoRect = video.getBoundingClientRect();

        const scaleX = video.videoWidth / videoRect.width;
        const scaleY = video.videoHeight / videoRect.height;

        const sourceX = (cropperRect.left - videoRect.left) * scaleX;
        const sourceY = (cropperRect.top - videoRect.top) * scaleY;
        const sourceWidth = cropperRect.width * scaleX;
        const sourceHeight = cropperRect.height * scaleY;

        canvas.width = cropperRect.width;
        canvas.height = cropperRect.height;

        ctx.drawImage(
          video,
          sourceX, sourceY, sourceWidth, sourceHeight,
          0, 0, canvas.width, canvas.height
        );
      }
    };

    const animationId = requestAnimationFrame(function animate() {
      updatePreview();
      requestAnimationFrame(animate);
    });

    return () => cancelAnimationFrame(animationId);
  }, [aspectRatio]);

  const handleMouseUp = () => {
    if (dragging) recordData();
    setDragging(false);

  };

  const handleMouseDown = (e) => {
    setDragging(true);
    // Get initial mouse position and box position
    setOffset({
      x: e.clientX - position.x,
      y: e.clientY - position.y,
    });
  };

  const recordData = () => {
    const newData = {
      timeStamp: currentTime,
      coordinates: [
        position.x,
        position.y,
        cropperSize.width,
        cropperSize.height
      ],
      volume,
      playbackRate
    };
    setRecordedData(prevData => [...prevData, newData]);
  };

  console.log('recordedData', recordedData);
  const handleMouseMove = (e) => {
    if (dragging) {
      const container = videoRef.current.getBoundingClientRect();
      const box = cropperRef.current.getBoundingClientRect();

      let newX = e.clientX - offset.x;
      let newY = e.clientY - offset.y;

      // Keep the box within container bounds
      if (newX < 0) newX = 0;
      if (newY < 0) newY = 0;
      if (newX + box.width > container.width) newX = container.width - box.width;
      if (newY + box.height > container.height) newY = container.height - box.height;

      setPosition({ x: newX, y: newY });

    }
  };

  const handleTimeUpdate = () => {
    setCurrentTime(videoRef.current.currentTime);
  };

  const handlePause = (videoRef) => {
    if (videoRef.current) {
      videoRef.current.pause();
      setIsPlaying(false);
    }
  };

  const handlePlay = (videoRef) => {
    if (videoRef.current) {
      videoRef.current.play();
      setIsPlaying(true);
    }
  };

  console.log('videoRef main', videoRef)
  const getVideoWidth = () => {
    return `${videoRef.current.getBoundingClientRect().height * aspectRatios[aspectRatio]}px`
  };

  const handleAspectRatioChange = (newRatio) => {
    setAspectRatio(newRatio);
    // Update cropper size based on new aspect ratio
    const videoHeight = videoRef.current.offsetHeight;
    const newWidth = videoHeight * aspectRatios[newRatio];
    setCropperSize({ width: newWidth, height: videoHeight });
  };

  const handleDownloadJSON = () => {
    const dataStr = "data:text/json;charset=utf-8," + encodeURIComponent(JSON.stringify(recordedData));
    const downloadAnchorNode = document.createElement('a');
    downloadAnchorNode.setAttribute("href", dataStr);
    downloadAnchorNode.setAttribute("download", "recorded_data.json");
    document.body.appendChild(downloadAnchorNode);
    downloadAnchorNode.click();
    downloadAnchorNode.remove();
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const editorUi = () => (
    <>
      <div className="video-container" onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}>
        <video
          className="video"
          ref={videoRef}
          src="http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4"
          onTimeUpdate={handleTimeUpdate}
        />
        {showCropper && <div
          id="draggable"
          ref={cropperRef}
          className="cropper"
          style={{
            top: `${position.y}px`,
            left: `${position.x}px`,
            width: getVideoWidth(),
            height: "100%",
            aspectRatio: aspectRatio,
          }}
          onMouseDown={handleMouseDown}
        />}
      </div>
      <div>
        <button type="button" onClick={isPlaying ? () => handlePause(videoRef) : () => handlePlay(videoRef)}>{isPlaying ? "Pause" : "Play"}</button>
        <input type="range" min="0" max={videoRef.current ? videoRef.current.duration : 0} value={currentTime} onChange={(e) => {
          videoRef.current.currentTime = e.target.value;
          setCurrentTime(e.target.value);
        }} />
        <select value={playbackRate} onChange={(e) => setPlaybackRate(Number(e.target.value))}>
          <option value={0.5}>0.5x</option>
          <option value={1}>1x</option>
          <option value={1.5}>1.5x</option>
          <option value={2}>2x</option>
        </select>
        <input
          type="range"
          min="0"
          max="1"
          step="0.1"
          value={volume}
          onChange={(e) => setVolume(Number(e.target.value))}
        />
        <select value={aspectRatio} onChange={(e) => handleAspectRatioChange(e.target.value)}>
          {Object.keys(aspectRatios).map((ratio) => (
            <option value={ratio}>{ratio}</option>
          ))}
        </select>
        <button onClick={handleDownloadJSON} className="px-4 py-2 bg-blue-500 text-white rounded flex items-center">
          Download JSON
        </button>
      </div>

    </>
  );

  return (
    <VideoFlipperContext.Provider value={{ 
      activeTab,
      handleTabClick,
      videoRef,
      aspectRatio
    }}>
      <div className="cropper-container">
        <Header activeTab={activeTab} onTabClick={handleTabClick} />
        {activeTab === 'editor' ? editorUi() : <RecordedPreview recordedData={recordedData} videoSrc={"http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4"} />}
      </div>
    </VideoFlipperContext.Provider>
  );
};

export default VideoFlipper;
