import { useEffect, useRef, useState } from "react";

import './VideoFlipper.css';

const aspectRatios = {
  "9:18": 0.5,
  "9:16": 9 / 16,
  "4:3": 4 / 3,
  "3:4": 3 / 4,
  "1:1": 1,
  "4:5": 0.8,
};

const VideoFlipper = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [playbackRate, setPlaybackRate] = useState(1);
  const [volume, setVolume] = useState(1);
  const [currentTime, setCurrentTime] = useState(0);
  const [aspectRatio, setAspectRation] = useState("9:18");
  const [showCropper, setShowCropper] = useState(false);
  const [cropperPosition, setCropperPosition] = useState({ x: 0, y: 0 });
  const videoRef = useRef(null);
  const cropperRef = useRef(null);

  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [offset, setOffset] = useState({ x: 0, y: 0 });

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.playbackRate = playbackRate;
      videoRef.current.volume = volume;
    }
  }, [playbackRate, volume]);

  useEffect(() => {
    if (videoRef.current) {
      setShowCropper(true);
    }
  }, []);

  const handlePause = () => {
    videoRef.current.pause();
    setIsPlaying(false);
  };

  const handlePlay = () => {
    videoRef.current.play();
    setIsPlaying(true);
  };

  // const handleCropperDrag = (e) => {
  //   const { height } = e.target.getBoundingClientRect();
  //   const video = videoRef.current.getBoundingClientRect();
  //   const upX = e.clientX - video.left;
  //   const upY = height >= video.height ? 0 : e.clientY - video.top;
  //   console.log(upX, upY);
  //   setCropperPosition({ x: upX, y: upY });
  // };

  const handleMouseDown = (e) => {
    setDragging(true);
    // Get initial mouse position and box position
    setOffset({
      x: e.clientX - position.x,
      y: e.clientY - position.y,
    });
  };

  const handleMouseMove = (e) => {
    if (dragging) {
      console.log('videoref', videoRef.current.getBoundingClientRect().width)
      // const container = document.getElementById('container').getBoundingClientRect();
      // const box = document.getElementById('draggable').getBoundingClientRect();

      // let newX = e.clientX - offset.x;
      // let newY = e.clientY - offset.y;

      // // Keep the box within container bounds
      // if (newX < 0) newX = 0;
      // if (newY < 0) newY = 0;
      // if (newX + box.width > container.width) newX = container.width - box.width;
      // if (newY + box.height > container.height) newY = container.height - box.height;

      // setPosition({ x: newX, y: newY });
    }
  };

  const getVideoWidth = () => {
    console.log('videoRef.current.getBoundingClientRect().width', videoRef.current.getBoundingClientRect().width, aspectRatios[aspectRatio])
    const width = videoRef.current.getBoundingClientRect().height * aspectRatios[aspectRatio];
    console.log('width', width)
    return `${videoRef.current.getBoundingClientRect().height * aspectRatios[aspectRatio]}px`
  };

  console.log('videoRef.currentvideoRef.current', videoRef.current)
  const handleMouseUp = () => {
    setDragging(false);
  };

  return (
    <>
      <div className="video-container" onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}>
        <video
          className="video"
          ref={videoRef}
          src="http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4"
        />
        {showCropper && <div
          id="draggable"
          ref={cropperRef}
          className="cropper"
          style={{
            top: `${position.y}px`,
            left: `${position.x}px`,
            width: getVideoWidth(),
            height: "100%",
            aspectRatio: aspectRatio,
          }}
          onMouseDown={handleMouseDown}
        />}

      </div>
      <div>
        <button type="button" onClick={isPlaying ? handlePause : handlePlay}>{isPlaying ? "Pause" : "Play"}</button>
        <input type="range" min="0" max={videoRef.current ? videoRef.current.duration : 0} value={currentTime} onChange={(e) => {
          videoRef.current.currentTime = e.target.value;
          setCurrentTime(e.target.value);
        }} />
        <select value={playbackRate} onChange={(e) => setPlaybackRate(Number(e.target.value))}>
          <option value={0.5}>0.5x</option>
          <option value={1}>1x</option>
          <option value={1.5}>1.5x</option>
          <option value={2}>2x</option>
        </select>
        <input
          type="range"
          min="0"
          max="1"
          step="0.1"
          value={volume}
          onChange={(e) => setVolume(Number(e.target.value))}
        />
        <select value={aspectRatio} onChange={(e) => setAspectRation(e.target.value)}>
          {Object.keys(aspectRatios).map((ratio) => (
            <option value={ratio}>{ratio}</option>
          ))}
        </select>
      </div>
    </>
  );
};

export default VideoFlipper;
