import { aspectRatios } from "./constant";
import useVideo from "./hooks/useVideo";

const Controls = ({ aspectRatio, setAspectRatio }) => {
  const {
    videoRef, handlePlay, handlePause, isPlaying, playbackRate, setPlaybackRate, volume, setVolume, currentTime, setCurrentTime
  } = useVideo();

  return (
    <div>
      <button type="button" onClick={isPlaying ? handlePause : handlePlay}>{isPlaying ? "Pause" : "Play"}</button>
      <input type="range" min="0" max={videoRef.current ? videoRef.current.duration : 0} value={currentTime} onChange={(e) => {
        videoRef.current.currentTime = e.target.value;
        setCurrentTime(e.target.value);
      }} />
      <select value={playbackRate} onChange={(e) => setPlaybackRate(Number(e.target.value))}>
        <option value={0.5}>0.5x</option>
        <option value={1}>1x</option>
        <option value={1.5}>1.5x</option>
        <option value={2}>2x</option>
      </select>
      <input
        type="range"
        min="0"
        max="1"
        step="0.1"
        value={volume}
        onChange={(e) => setVolume(Number(e.target.value))}
      />
      <select value={aspectRatio} onChange={(e) => setAspectRatio(e.target.value)}>
        {Object.keys(aspectRatios).map((ratio) => (
          <option value={ratio}>{ratio}</option>
        ))}
      </select>
    </div>
  )
};

export default Controls;
